import { Link, useParams } from "react-router-dom";
import { useEffect, useState } from "react";

import CategoryService from "../../services/category.service";

export const CategortOffersList = () => {
  const params = useParams();

  const [offers, setOffers] = useState([]);

  useEffect(() => {
    CategoryService.getCategoryById(params.id).then((category) =>
      setOffers(category.data.offers)
    );
  }, []);

  console.log(offers);

  return (
    <div class="category-offer-list">
      <div class="container mt-100">
        <div class="offer_list col-8">
          <div class="list-group" id="list-tab" role="tablist">
            {offers.map((offer) => (
              <Link to={`/offer/details/${offer.id}`}>
                <h4> {offer.title} </h4>
                Price: {offer.price}
              </Link>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};
