import { Link, useParams } from "react-router-dom";
import { useEffect, useState } from "react";

import CategoryService from "../../services/category.service";

export const CategortOffersList = () => {
  const params = useParams();

  const [offers, setOffers] = useState([]);

  useEffect(() => {
    CategoryService.getCategoryById(params.id).then((category) =>
      setOffers(category.data.offers)
    );
  }, []);

  return (
    <div class="category-offer-list">
      <div class="container mt-100">
        <div class="offer_list col-8">
          <div class="list-group" id="list-tab" role="tablist">
            {offers.length === 0 && (
              <div className="searchResult">
                <img src="https://assets.website-files.com/61a3c085c8291d29d5228353/61a3cd010fa4a56cd1d7cf4a_6182c5fecf70270072c4ee97_no-results-blog.jpeg" />
              </div>
            )}

            {offers.map((offer) => (
              <Link to={`/offer/details/${offer.id}`}>
                <a
                  class="list-group-item list-group-item-action"
                  id="list-settings-list"
                  data-toggle="list"
                  href="#list-settings"
                  role="tab"
                  aria-controls="settings"
                  key={offer.id}
                >
                  <h4> {offer.title} </h4>
                  Price: {offer.price}
                </a>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};
