import axios from "axios";
import authHeader from "./auth-header";

import { useContext } from "react";

import { ShoppingCartContext } from "../contexts/ShoppingCartContext";

const API_URL = "http://localhost:8080/api/shopping-card/";

class CheckoutService {
  async addOfferToCard(data) {
    const { onUpdate } = useContext(ShoppingCartContext);
    return axios
      .post(API_URL + "add", data, {
        headers: authHeader(),
      })
      .then(() => onUpdate());
  }
  getAllOffersForCurrentUser(userId) {
    return axios.get(`${API_URL}${userId}`, {
      headers: authHeader(),
    });
  }

  soldOffers() {
    return axios.delete(API_URL + "checkout", {
      headers: authHeader(),
    });
  }
}
export default new CheckoutService();
