import { useEffect, useState } from "react";
import "./MoreProducts.css";
import { MoreProductView } from "./MoreProductView";
import OfferService from "../../../services/offer.service";

export const MoreProducts = ({ params }) => {
  const [offers, setoffers] = useState([]);

  const id = params.id;

  const reduceOffers = offers.filter((offer) => offer.id !== id);

  console.log(reduceOffers);

  useEffect(() => {
    OfferService.getAllOffers().then((offer) => setoffers(offer.data));
  }, []);

  return (
    <>
      <div className="more-products-wrapper">
        {offers
          .splice(0, 3)
          .filter((offer) => offer.id !== params.id)
          .map((offer) => (
            <MoreProductView offer={offer} />
          ))}
      </div>
    </>
  );
};
